import csv
import json
import re

def convert_csv_to_json(csv_string, output_file='data.json'):
    # Membuat list untuk menyimpan data
    data = []
    
    # Memproses setiap baris menggunakan regex
    pattern = r'"([^"]*)", "([^"]*)"'
    matches = re.findall(pattern, csv_string)
    
    # Membuat dictionary untuk setiap pasangan question-answer
    for question, answer in matches:
        data.append({
            "question": question,
            "answer": answer
        })
    
    # Menyimpan ke file JSON
    with open(output_file, 'w', encoding='utf-8') as f:
        json.dump(data, f, indent=2, ensure_ascii=False)
    
    print(f"Data berhasil disimpan ke dalam file '{output_file}'")
    
    # Mengembalikan data JSON sebagai string (opsional, untuk preview)
    return json.dumps(data, indent=2, ensure_ascii=False)

